search:
  color: rgba(246, 233, 215, 0.78);
  background: rgba(8, 11, 20, 0.68);
  border: 1px solid rgba(255, 216, 127, 0.18);
  box-shadow: 0 14px 30px rgba(0, 0, 0, 0.45);
  pointer-events: none;
  z-index: 1;
}

replace:
  color: rgba(246, 233, 215, 0.78);
  background: rgba(8, 11, 20, 0.68);
  border: 1px solid rgba(255, 216, 127, 0.18);
  box-shadow:
    0 14px 30px rgba(0, 0, 0, 0.45),
    0 0 0px rgba(255, 210, 135, 0);
  animation: versionGlow 2.7s ease-in-out infinite;
  pointer-events: none;
  z-index: 1;
}

search:
@keyframes subtitlePulse {
  0% {
    opacity: 0.85;
    transform: translateY(0);
  }
  100% {
    opacity: 1;
    transform: translateY(-2px);
  }
}

replace:
@keyframes subtitlePulse {
  0% {
    opacity: 0.85;
    transform: translateY(0);
  }
  100% {
    opacity: 1;
    transform: translateY(-2px);
  }
}

@keyframes versionGlow {
  0%,
  70%,
  100% {
    box-shadow:
      0 14px 30px rgba(0, 0, 0, 0.45),
      0 0 0px rgba(255, 210, 135, 0);
  }
  85% {
    box-shadow:
      0 14px 30px rgba(0, 0, 0, 0.45),
      0 0 14px rgba(255, 210, 135, 0.35),
      0 0 28px rgba(255, 210, 135, 0.2);
  }
}
