search:
.title-screen-version {
  position: absolute;
  bottom: 18px;
  right: 22px;
  padding: 0.35rem 0.7rem;
  border-radius: 999px;
  font-size: 0.85rem;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  color: rgba(246, 233, 215, 0.78);
  background: rgba(8, 11, 20, 0.68);
  border: 1px solid rgba(255, 216, 127, 0.18);
  box-shadow:
    0 14px 30px rgba(0, 0, 0, 0.45),
    0 0 0px rgba(255, 210, 135, 0);
  animation: versionGlow 2.7s ease-in-out infinite;
  pointer-events: none;
  z-index: 1;
}

