@keyframes float-damage-number {
  0% {
    opacity: 1;
    transform: translate(-50%, 0) scale(1);
  }
  60% {
    opacity: 1;
    transform: translate(-50%, -6px) scale(1.02);
  }
  100% {
    opacity: 0;
    transform: translate(-50%, -24px) scale(1.05);
  }
}

.floating-damage-number {
  position: absolute;
  left: 50%;
  color: #ff6b6b;
  font-weight: 800;
  text-shadow: 0 0 6px rgba(0, 0, 0, 0.8);
  transform: translateX(-50%);
  animation: float-damage-number 1.05s ease-out forwards;
  pointer-events: none;
  z-index: 12;
}

