.battlefield-unit--current {
  box-shadow: 0 0 6px 2px rgba(255, 235, 59, 0.7);
}

@keyframes unit-shake {
  0% {
    transform: translateX(0);
  }
  20% {
    transform: translateX(-3px);
  }
  40% {
    transform: translateX(3px);
  }
  60% {
    transform: translateX(-2px);
  }
  80% {
    transform: translateX(2px);
  }
  100% {
    transform: translateX(0);
  }
}

.unit-shake {
  animation: unit-shake 300ms ease-in-out;
}

